import React from "react";
import "./Definitions.css";

const Definitions = (meanings, word, language, poSpeech) => {
  console.log(meanings.meanings);

  return (
    <div className="meanings">
      {meanings.meanings.map((item) =>
        item.meanings.map((i) => {
          if (i.partOfSpeech === poSpeech)
            return 
            <h>{i.partOfSpeech}</h>
          return i.definitions.map((def) => (
            <div>
              <b>{def.definition} </b>

              {def.example && <b>Example: {def.example} </b>}
            </div>
          ));;
        })
      )}
    </div>
  );
};
export default Definitions;
