import React from "react";
import "./Definitions.css";

const Definitions = (meanings, word, language, poSpeech) => {
  return (

    <div className="meanings">

      {word === "" ? (
        <span >Type a Word</span>
      ) : (
        meanings.map((item) =>
          item.meanings.map((i) =>
            i.definitions.map((def) => (
              <div>
                <b>{def.definition}</b>

                {def.example && (
                  <span>
                    <b>Example :</b> {def.example}
                  </span>
                )}
              </div>
            ))
          )
        )
      )}
    </div>
  );
};
export default Definitions;
